mapper.select:
select

from sys_user
where email = #{email,jdbcType=VARCHAR} and pswd = #{pswd,jdbcType=VARCHAR}

mapper.insert:
insert into sys_user (id, nickname, email,
pswd, create_time, last_login_time,status,salt
)
values (#{id,jdbcType=BIGINT}, #{nickname,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR},
#{pswd,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{lastLoginTime,jdbcType=TIMESTAMP},#{status,jdbcType=BIGINT},#{salt,jdbcType=VARCHAR}
)
